/* src/components/About.jsx */
import React from 'react';

function About() {
  return (
    <section className="py-20 px-4 bg-gray-100">
      <div className="max-w-4xl mx-auto text-center">
        <h2 className="text-4xl font-bold text-dodgers">Our Mission</h2>
        <p className="mt-6 text-lg">
          Half Full is a community for veterans and creatives, blending mental health support with gaming and storytelling to inspire hope and healing.
        </p>
      </div>
    </section>
  );
}

export default About;
